    private void buttonAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonAddActionPerformed
        Vendor vendor=new Vendor();
        vendor.setName(textName.getText());
        vendor.setAddress(textAddress.getText());
        vendor.setContact1(textContact1.getText());
        vendor.setContact2(textContact2.getText());
        vendor.setEmailId(textEmailId.getText());
        
        Integer vendorId=new VendorClient().add(vendor);
        if(vendorId>0){
            JOptionPane.showMessageDialog(null, "Vendor details added successfully \n Id of Vendor is : "+vendorId);
            clearText();
        }else{
            JOptionPane.showMessageDialog(null, "Sorry ! Unable to add the Vendor details");
        }
        
    }//GEN-LAST:event_buttonAddActionPerformed

