    private void loadTable(List<VendorItem> vendorBillItems){
        
        DefaultTableModel defaultTableModel=(DefaultTableModel)tableItems.getModel();
        vendorBillItems.forEach(vi->{
            Object[] rowData={
                vi.getName(),
                vi.getPurchasePrice(),
                vi.getDiscount(),
                vi.getQuantity(),
                vi.getTotalPurchaseAmount()
            };
            defaultTableModel.addRow(rowData);
        });
        
        
    }
     
