    private void setup(){
        vendorList=new VendorClient().get().stream().map(vo->(Vendor)vo).collect(Collectors.toList());
        
        ActionListener comboActionListener=comboVendorList.getActionListeners()[0];
        comboVendorList.removeActionListener(comboActionListener);
        comboVendorList.removeAllItems();
        vendorList.forEach(v->comboVendorList.addItem(v.getName()));
        
        comboVendorList.addActionListener(comboActionListener);
        
        vendorBills=null;
        
    }
    
    private void viewVendorBills(String vendorName){
        Integer vendorId=vendorList.stream().filter(v->v.getName().equalsIgnoreCase(vendorName)).findFirst().get().getId();
        vendorBills=new VendorBillClient().getByVendorID(vendorId);
        loadTableData(vendorBills);
    }
    
    private void loadTableData(List<VendorBill> vendorBillList){
        
        
        
        DefaultTableModel tableModel=(DefaultTableModel)tableVendorBills.getModel();
        while (tableModel.getRowCount()>0) {
            tableModel.removeRow(0);
        }
        vendorBillList.forEach(vendorBill->{
            Object[] rowData={
                                vendorBill.getBillId(),
                                new Date(),
                                vendorBill.getTotalPurchaseAmount(),
                                vendorBill.getTotalDiscountAmount(),
                                vendorBill.getTotalBillAmount(),
                                vendorBill.getTotalPayableBillAmount(),
                                vendorBill.getVat(),
                                vendorBill.getVendorName()
                             };
            tableModel.addRow(rowData);
        });
    }
    
    
