    private void comboVendorListActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comboVendorListActionPerformed
        String vendorNameSelected=comboVendorList.getSelectedItem().toString();
        viewVendorBills(vendorNameSelected);
    }//GEN-LAST:event_comboVendorListActionPerformed

    private void tableVendorBillsKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_tableVendorBillsKeyPressed
        if (evt.getKeyCode() == KeyEvent.VK_ENTER) {
            int selectedRow=tableVendorBills.getSelectedRow();
            Long billId=(Long)tableVendorBills.getValueAt(selectedRow, 0);
            
            List<VendorItem> vendorBillItems=vendorBills.stream()
                                                        .filter(vb->vb.getBillId()==billId)
                                                        .findFirst()
                                                        .get().getBillItemList();
            
            JFrame f=new  JFrame();
            f.add(new VendorBillItems(vendorBillItems));
            f.setSize(600, 300);
            f.setVisible(true);
        }
    }//GEN-LAST:event_tableVendorBillsKeyPressed


